"use strict";

function afterLoad() {
  try {
    const singleton = require('./singleton');

    const assertTrackingService = singleton.singleton.triggerServices.getService("AssertTrackingService");

    if (assertTrackingService.aug) {
      assertTrackingService.hookOnAsserts();
    }

    return true;
  } catch (e) {}

  return false;
}

const success = afterLoad();

if (success) {
  afterAll(async () => {
    await new Promise(r => setTimeout(r, 2000));
  });
}